    private void jButtonSumaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonSumaActionPerformed
        //Deshabilita el boton de sumar al pusarlo
        jButtonMultiplicar.setEnabled(true);
        jButtonDividir.setEnabled(true);
        jButtonResta.setEnabled(true);
        jButtonSuma.setEnabled(false);
    }//GEN-LAST:event_jButtonSumaActionPerformed

    private void jButtonRestaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonRestaActionPerformed
        //Deshabilita el boton de restar al pulsarlo
        jButtonMultiplicar.setEnabled(true);
        jButtonDividir.setEnabled(true);
        jButtonResta.setEnabled(false);
        jButtonSuma.setEnabled(true);
    }//GEN-LAST:event_jButtonRestaActionPerformed

    private void jButtonMultiplicarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonMultiplicarActionPerformed
        //Deshabilita el boton de multiplicar al pulsarlo
        jButtonMultiplicar.setEnabled(false);
        jButtonDividir.setEnabled(true);
        jButtonResta.setEnabled(true);
        jButtonSuma.setEnabled(true);
    }//GEN-LAST:event_jButtonMultiplicarActionPerformed

    private void jButtonDividirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonDividirActionPerformed
        //Deshabilita el boton de dividir al pulsarlo
        jButtonMultiplicar.setEnabled(true);
        jButtonDividir.setEnabled(false);
        jButtonResta.setEnabled(true);
        jButtonSuma.setEnabled(true);
    }//GEN-LAST:event_jButtonDividirActionPerformed

    private void jButtonResultadoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonResultadoActionPerformed
        //Cogemos los numeros 1 y 2 escritos
        double nuum1 = Integer.valueOf(jTextFieldNum1.getText());
        double nuum2 = Integer.valueOf(jTextFieldNum2.getText());
        //Variables de las operaciones que vayamos a realizar
        double suma = nuum1 + nuum2;
        double resta = nuum1 - nuum2;
        double multiplicacion = nuum1 * nuum2;
        double division = nuum1/nuum2;
         
        //Sentencia if seg√∫n el boton deshabilitado
        if(!jButtonResta.isEnabled()){
           jTextFieldResultado.setText(String.valueOf(resta));
        }else  
            if(!jButtonSuma.isEnabled()){
            jTextFieldResultado.setText(String.valueOf(suma));
        }else
            if(!jButtonMultiplicar.isEnabled()){
            jTextFieldResultado.setText(String.valueOf(multiplicacion));
        }else
            if(!jButtonDividir.isEnabled()){
            jTextFieldResultado.setText(String.valueOf(division));
            } 
    }//GEN-LAST:event_jButtonResultadoActionPerformed

